/**
 * @brief 启动WiFi混杂模式监听
 * 
 * @param[in] cb 数据包回调函数
 * @param[in] filter 数据包过滤条件
 * @return int 操作结果，OPRT_OK表示成功
 * 
 * @note 该函数会配置WiFi混杂模式过滤器，设置接收回调，并启用混杂模式
 */
static int wifi_sniffer_start(wifi_promiscuous_cb_t cb, wifi_promiscuous_filter_t *filter)
{
    esp_wifi_set_promiscuous_filter(filter);
    esp_wifi_set_promiscuous_rx_cb(cb);
    esp_wifi_set_promiscuous(true);
    return OPRT_OK;
}

/**
 * @brief 停止WiFi混杂模式
 * 
 * 关闭ESP32的WiFi混杂模式并清除接收回调函数。
 * 
 * @return int 返回操作结果，OPRT_OK表示成功
 */
static int wifi_sniffer_stop(void)
{
    esp_wifi_set_promiscuous(false);
    esp_wifi_set_promiscuous_rx_cb(NULL);
    return OPRT_OK;
}
