OPERATE_RET tkl_wifi_get_connected_ap_info(FAST_WF_CONNECTED_AP_INFO_T **fast_ap_info)
{
    wifi_ap_record_t ap_info;
    FAST_WF_CONNECTED_AP_INFO_T *ptr;

    memset(&ap_info, 0, sizeof(ap_info));
    if (ESP_OK != esp_wifi_sta_get_ap_info(&ap_info)) {
        //ESP_LOGE(DBG_TAG, "%s: call esp_wifi_sta_get_ap_info failed", __func__);
        *fast_ap_info = NULL;
        return OPRT_COM_ERROR;
    }

    if (!strncmp((char *)ap_conn_info.ssid, (char *)ap_info.ssid, TKL_WIFI_SSID_LEN)) {
        ap_conn_info.authmode = ap_info.authmode;
        ap_conn_info.channel = ap_info.primary;
    }

    //ESP_LOGI(DBG_TAG, "%s: ssid %s password %s authmode %d channel %d",
    //    __func__, ap_conn_info.ssid, ap_conn_info.password, ap_conn_info.authmode, ap_conn_info.channel);

    ptr = (FAST_WF_CONNECTED_AP_INFO_T *)tkl_system_malloc(sizeof(FAST_WF_CONNECTED_AP_INFO_T) + sizeof(ap_conn_info));
    if (NULL == ptr) {
        //ESP_LOGE(DBG_TAG, "%s: malloc memory failed", __func__);
        *fast_ap_info = NULL;
        return OPRT_COM_ERROR;
    }

    ptr->len = sizeof(ap_conn_info);
    memcpy(ptr->data, &ap_conn_info, sizeof(ap_conn_info));

    *fast_ap_info = ptr;
    return OPRT_OK;
}

