OPERATE_RET tkl_wifi_station_fast_connect(const FAST_WF_CONNECTED_AP_INFO_T *fast_ap_info)
{
    wifi_config_t wifi_cfg;
    tkl_wifi_ap_conn_info_s *ptr;

    assert(NULL != fast_ap_info);

    ptr = (tkl_wifi_ap_conn_info_s *)fast_ap_info->data;

    memset(&wifi_cfg, 0, sizeof(wifi_cfg));
    strncpy((char *)wifi_cfg.sta.ssid, (char *)ptr->ssid, TKL_WIFI_SSID_LEN);
    strncpy((char *)ap_conn_info.ssid, (char *)ptr->ssid, TKL_WIFI_SSID_LEN);

    if (ptr->password[0] != '\0') {
        strncpy((char *)wifi_cfg.sta.password, (char *)ptr->password, TKL_WIFI_PASSWD_LEN);
        strncpy((char *)ap_conn_info.password, (char *)ptr->password, TKL_WIFI_PASSWD_LEN);
    }

    wifi_cfg.sta.scan_method = WIFI_FAST_SCAN;
    wifi_cfg.sta.channel = ptr->channel;
    wifi_cfg.sta.listen_interval = TKL_WIFI_LISTEN_INTERVAL_DEF;
    wifi_cfg.sta.threshold.authmode = WIFI_AUTH_WPA_WPA2_PSK;
    wifi_cfg.sta.pmf_cfg.capable = true;
    wifi_cfg.sta.pmf_cfg.required = false;
    wifi_cfg.sta.sae_pwe_h2e = WPA3_SAE_PWE_BOTH;

    if (ESP_OK != esp_wifi_stop()) {
        //ESP_LOGI(DBG_TAG, "%s: call esp_wifi_stop failed", __func__);
        //return OPRT_COM_ERROR;
    }

    if (ESP_OK != esp_wifi_set_default_wifi_sta_handlers()) {
        //ESP_LOGE(DBG_TAG, "%s: call esp_wifi_set_default_wifi_sta_handlers failed", __func__);
        return OPRT_COM_ERROR;
    }

    if (ESP_OK != esp_wifi_set_mode(WIFI_MODE_STA)) {
        //ESP_LOGE(DBG_TAG, "%s: call esp_wifi_set_mode failed", __func__);
        return OPRT_COM_ERROR;
    }
    if (ESP_OK != esp_wifi_set_config(ESP_IF_WIFI_STA, &wifi_cfg)) {
        //ESP_LOGE(DBG_TAG, "%s: call esp_wifi_set_config failed", __func__);
        return OPRT_COM_ERROR;
    }
    if (ESP_OK != esp_wifi_start()) {
        //ESP_LOGE(DBG_TAG, "%s: call esp_wifi_start failed", __func__);
        return OPRT_COM_ERROR;
    }

    esp_wifi_set_ps(WIFI_PS_MIN_MODEM);

    //ESP_LOGI(DBG_TAG, "%s: fast connect to ap SSID:%s password:%s channel %d",
    //    __func__, wifi_cfg.sta.ssid, wifi_cfg.sta.password, wifi_cfg.sta.channel);

    return OPRT_OK;
}
